import { useState, userEffect, useEffect } from "react"
import { Container, Row, Col } from "react-bootstrap"
import { ArrowRightCircle } from "react-bootstrap-icons"
import headerImg from "../assets/img/header-img.svg"

export const Banner = () => {
  const [loopNum, setLoopNum] = useState(0)
  const [isDeleting, setisDeleting] = useState(false)
  const toRotate = ["Master's Student", "Test Rotate"]
  const [text, setText] = useState("")
  const period = 2000
  const [delta, setDelta] = useState(300 - Math.random() * 100)

  useEffect(() => {
    const ticker = setInterval(() => {
      tick()
    }, delta)

    return () => {
      clearInterval(ticker)
    }
  }, [text])

  const tick = () => {
    let i = loopNum % toRotate.length
    let fullText = toRotate[i]
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1)

    setText(updatedText)

    if (isDeleting) {
      setDelta((prevDelta) => prevDelta / 2)
    }

    if (!isDeleting && updatedText === fullText) {
      setisDeleting(true)
      setDelta(period)
    } else if (isDeleting && updatedText === "") {
      setisDeleting(false)
      setLoopNum(loopNum + 1)
      setDelta(500)
    }
  }

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="align-item-center">
          <Col xs={12} md={6} xl={7}>
            <span className="tagline">Welcome to my portfolio!</span>
            <h1>
              {`Hi I'm Daniel `}
              <span className="wrap">{text}</span>
            </h1>
            <p>
              I am a Master's student at Aalto University. I study the programme
              in Computer, Communication and Information Sciences and my major
              is Human-Computer Interaction (HCI) and minor is Computer Science
              (SC).
            </p>
            <button onClick={() => console.log("BUTTON CLICKED::BANNER")}>
              Linkedin <ArrowRightCircle size={25} />
            </button>
            <Col xs={12} md={6} xl={5}>
              <img src={headerImg} alt="header image" />
            </Col>
          </Col>
        </Row>
      </Container>
    </section>
  )
}
