import { Container, Row, Col, Tab, Nav } from "react-bootstrap"
import { ProjectCard } from "./ProjectCard"
import bgImg2 from "../assets/img/background/bg-color2.png"
import bachelorsThesisIcon from "../assets/img/projects/bachelors-thesis-icon.png"
import planeWarfareIcon from "../assets/img/projects/plane-warfare-icon.png"
import platformerIcon from "../assets/img/projects/platformer-icon.png"
import towerDefenseIcon from "../assets/img/projects/tower-defense-icon.png"
import quizzerIcon from "../assets/img/projects/quizzer-icon.png"
import TrackVisibility from "react-on-screen"

export const Projects = () => {
  const projects = [
    {
      title: "Qu?zzer",
      description:
        "Quiz web application created on the course Web Software Development. The application uses Deno and Oak and has a three-tier architecture: client, server and database. In addition, The application has four layers: views, controllers, services, database.",
      imgUrl: quizzerIcon,
    },
    {
      title: "Tower Defense",
      description: "",
      imgUrl: towerDefenseIcon,
    },
    {
      title: "Platformer",
      description: platformerIcon,
      imgUrl: "",
    },
    {
      title: "Plane Warfare",
      description: "",
      imgUrl: planeWarfareIcon,
    },
  ]

  const thesises = [
    {
      title: "Bachelor's Thesis",
      description: "",
      imgUrl: bachelorsThesisIcon,
    },
  ]

  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col>
            <h2>Projects</h2>
            <p>Description.</p>
            <Tab.Container id="projects-tabs" defaultActiveKey="first">
              <Nav
                variant="pills"
                className="nav-pills mb-5 justify-content-center align-items-center"
                id="pills-tab"
              >
                <Nav.Item>
                  <Nav.Link eventKey="first">Coding projects</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="second">Thesis</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="third">Other</Nav.Link>
                </Nav.Item>
              </Nav>
              <Tab.Content
                id="slideInUp"
                className={
                  isVisible ? "animate__animated animate__slideInUp" : ""
                }
              >
                <Tab.Pane eventKey="first">
                  <Row>
                    {projects.map((project, index) => {
                      return (
                        <ProjectCard
                          key={index}
                          title={project.title}
                          description={project.description}
                          imgUrl={project.imgUrl}
                        />
                      )
                    })}
                  </Row>
                </Tab.Pane>
                <Tab.Pane eventKey="second">Test2</Tab.Pane>
                <Row>
                  {thesises.map((thesis, index) => {
                    return (
                      <ProjectCard
                        key={index}
                        title={thesis.title}
                        description={thesis.description}
                        imgUrl={thesis.imgUrl}
                      />
                    )
                  })}
                </Row>
                <Tab.Pane eventKey="third">Test3</Tab.Pane>
              </Tab.Content>
            </Tab.Container>
          </Col>
        </Row>
      </Container>
      <img
        className="background-image-right"
        src={bgImg2}
        alt="Background coloring"
      />
    </section>
  )
}
